package org.operators.com;

public class UnaryOperators01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		System.out.println(i++);
		System.out.println(i);
		System.out.println(i--);
		System.out.println(++i);
		System.out.println(--i);
		System.out.println(i++);

	}

}
